update walletdb
SET walletdb.assetCoins = walletdb.assetCoins + #{amountToBeAdded}
WHERE walletdb.walletId = #{walletId} AND walletdb.assetName = #{assetName}

update walletdb
SET walletdb.assetCoins =  walletdb.assetCoins - #{withdrawalAmount}
WHERE walletdb.walletId = #{walletId} AND walletdb.assetName = #{assetName}
